import { redirect } from "next/navigation";
import { auth } from "@/registry/auth/lib/auth";
import {
  Card,
  CardContent,
  CardHeader,
  CardTitle,
} from "@/registry/new-york/ui/card";
import { Button } from "@/registry/new-york/ui/button";
import { UserInfo } from "@/registry/auth/components/user-info";

export default async function UserDashboard() {
  const session = await auth();

  if (!session) {
    redirect("/auth/signin");
  }

  return (
    <div className="max-w-6xl mx-auto py-8 px-4">
      <h1 className="text-3xl font-bold mb-8">User Dashboard</h1>

      <div className="grid gap-6 md:grid-cols-2">
        <UserInfo user={session?.user} label="" />

        <Card className="shadow-md">
          <CardHeader>
            <CardTitle>Account Settings</CardTitle>
          </CardHeader>
          <CardContent>
            <p className="text-sm text-muted-foreground mb-4">
              Manage your account settings and preferences.
            </p>
            <div className="space-y-2">
              <Button className="w-full" asChild variant="outline">
                <a href="/profile">Edit Profile</a>
              </Button>
              {!session?.user?.isOAuth && (
                <Button className="w-full" asChild variant="outline">
                  <a href="/profile/change-password">Change Password</a>
                </Button>
              )}
            </div>
          </CardContent>
        </Card>
      </div>

      {session.user.role === "ADMIN" && (
        <div className="mt-6">
          <Card className="shadow-md">
            <CardHeader>
              <CardTitle>Admin Access</CardTitle>
            </CardHeader>
            <CardContent>
              <p className="text-sm text-muted-foreground mb-4">
                You have admin privileges. You can access the admin dashboard to
                manage users and system settings.
              </p>
              <Button asChild>
                <a href="/admin">Go to Admin Dashboard</a>
              </Button>
            </CardContent>
          </Card>
        </div>
      )}
    </div>
  );
}

import { auth } from "@/registry/auth/lib/auth";

export const currentUser = async () => {
  const session = await auth();
  return session?.user;
};

export const currentRole = async () => {
  const session = await auth();
  return session?.user?.role;
};
export const isCurrentAccountOAuth = async () => {
  const session = await auth();
  return session?.user?.isOAuth;
};

import { ExtendedUser } from "@/registry/auth/lib/next-auth";
import { Badge } from "@/registry/new-york/ui/badge";
import { Card, CardContent, CardHeader } from "@/registry/new-york/ui/card";
import { currentUser } from "@/registry/auth/lib/get-user";
import {
  Avatar,
  AvatarFallback,
  AvatarImage,
} from "@/registry/new-york/ui/avatar";
import { User } from "lucide-react";

interface UserInfoProps {
  user?: ExtendedUser;
  label: string;
}

export async function UserInfo({ user, label }: UserInfoProps) {
  const userData = await currentUser();
  return (
    <Card className="shadow-md">
      <CardHeader>
        <p className="text-center text-2xl font-semibold">{label}</p>
      </CardHeader>
      <CardContent className="space-y-4">
        <div className="flex flex-row items-center justify-between rounded-lg border p-3 shadow-sm">
          <p className="text-sm font-medium">ID</p>
          <p className="text-sm text-muted-foreground">{user?.id}</p>
        </div>
        <div className="flex flex-row items-center justify-between rounded-lg border p-3 shadow-sm">
          <p className="text-sm font-medium">Name</p>
          <p className="mtext-sm text-muted-foreground">{user?.name}</p>
        </div>

        <Avatar>
          <AvatarImage src={user?.image || ""} />
          <AvatarFallback className="bg-sky-500">
            <User className="text-white" />
          </AvatarFallback>
        </Avatar>

        <div className="flex flex-row items-center justify-between rounded-lg border p-3 shadow-sm">
          <p className="text-sm font-medium">Email</p>
          <p className="text-sm text-muted-foreground">{user?.email}</p>
        </div>
        <div className="flex flex-row items-center justify-between rounded-lg border p-3 shadow-sm">
          <p className="text-sm font-medium">Role</p>
          <p className="text-sm text-muted-foreground">{user?.role}</p>
        </div>
        {!userData?.isOAuth && (
          <div className="flex flex-row items-center justify-between rounded-lg border p-3 shadow-sm">
            <p className="text-sm font-medium">Two Factor Authentication</p>
            <Badge
              variant={user?.isTwoFactorEnabled ? "default" : "destructive"}
            >
              {user?.isTwoFactorEnabled ? "ON" : "OFF"}
            </Badge>
          </div>
        )}
      </CardContent>
    </Card>
  );
}

"use client";

import * as React from "react";
import { cva, type VariantProps } from "class-variance-authority";
import { cn } from "@/lib/utils";

const badgeVariants = cva(
  "inline-flex items-center rounded-md border px-2.5 py-0.5 text-xs font-semibold transition-colors focus:outline-none focus:ring-2 focus:ring-ring focus:ring-offset-2",
  {
    variants: {
      variant: {
        default:
          "border-transparent bg-primary text-primary-foreground shadow hover:bg-primary/80",
        secondary:
          "border-transparent bg-secondary text-secondary-foreground hover:bg-secondary/80",
        destructive:
          "border-transparent bg-destructive text-destructive-foreground shadow hover:bg-destructive/80",
        outline: "text-foreground",
      },
    },
    defaultVariants: {
      variant: "default",
    },
  },
);

// 2. Badge Props
export interface BadgeProps
  extends React.HTMLAttributes<HTMLDivElement>,
    VariantProps<typeof badgeVariants> {}

const Badge = React.forwardRef<HTMLDivElement, BadgeProps>(
  ({ className, variant, ...props }, ref) => {
    return (
      <div
        ref={ref}
        className={cn(badgeVariants({ variant }), className)}
        {...props}
      />
    );
  },
);
Badge.displayName = "Badge";

export { Badge, badgeVariants };
