          <div className="space-y-2">
            <Label htmlFor="content" className="text-base">
              Newsletter Content
            </Label>
            <Textarea
              id="content"
              name="content"
              placeholder="Enter your newsletter content here. HTML is supported."
              className="min-h-[300px] resize-none"
              required
