            {state.errors?.content && (
              <p className="text-sm text-destructive">
                {state.errors.content[0]}
              </p>
            )}
          </div>

          {state.message && !state.success && (
            <Alert variant="destructive">
              <AlertDescription>{state.message}</AlertDescription>
            </Alert>
          )}

          <Button
            type="submit"
            className="h-12 px-8 text-base transition-all duration-200 group"
            disabled={isPending}
          >
            {isPending ? (
              <>
                <Loader2 className="mr-2 h-4 w-4 animate-spin" />
                Sending Newsletter...
              </>
            ) : (
              <>
                <Send className="mr-2 h-4 w-4" />
                Send Newsletter
              </>
            )}
          </Button>
        </form>
      )}
    </div>
  );
}

"use client";

import { useActionState } from "react";
import { Button } from "@/registry/new-york/ui/button";
import { Input } from "@/registry/new-york/ui/input";
import { Label } from "@/registry/new-york/ui/label";
import {
  subscribeToNewsletter,
  type SubscribeFormState,
} from "@/registry/new-york/dynamic-components/subscribe-newsletter/actions/subscribe-newsletter-actions";
import { Loader2, CheckCircle, Mail } from "lucide-react";
import {
  Alert,
  AlertDescription,
  AlertTitle,
} from "@/registry/new-york/ui/alert";

const initialState: SubscribeFormState = {};

export default function NewsletterSubscription() {
  const [state, formAction, isPending] = useActionState(
    subscribeToNewsletter,
    initialState
  );

  return (
    <section className="w-full py-12 md:py-24 lg:py-32 bg-muted/30">
      <div className="container px-4 md:px-6">
        <div className="flex flex-col items-center justify-center space-y-4 text-center">
          <div className="space-y-2">
            <h2 className="text-3xl font-bold tracking-tighter sm:text-4xl md:text-5xl">
              Stay Updated
            </h2>
            <p className="max-w-[700px] text-muted-foreground md:text-xl/relaxed">
              Subscribe to our newsletter to receive the latest updates and
              exclusive offers.
            </p>
          </div>
        </div>
        <div className="mx-auto max-w-[500px] mt-8">
          {state.success ? (
            <Alert className="bg-primary/10 border-primary/20">
              <CheckCircle className="h-4 w-4 text-primary" />
              <AlertTitle>Success!</AlertTitle>
              <AlertDescription>{state.message}</AlertDescription>
            </Alert>
          ) : (
            <form action={formAction} className="space-y-4">
              <div className="space-y-2">
                <Label htmlFor="name" className="text-base">
                  Name (Optional)
                </Label>
                <Input
                  id="name"
                  name="name"
                  placeholder="Your name"
                  className="h-12 bg-background border-muted-foreground/20 focus-visible:ring-offset-0"
                />
                {state.errors?.name && (
                  <p className="text-sm text-destructive">
                    {state.errors.name[0]}
                  </p>
                )}
              </div>
              <div className="space-y-2">
                <Label htmlFor="email" className="text-base">
                  Email
                </Label>
                <Input
                  id="email"
                  name="email"
                  type="email"
                  placeholder="your.email@example.com"
                  required
                  className="h-12 bg-background border-muted-foreground/20 focus-visible:ring-offset-0"
                />
                {state.errors?.email && (
                  <p className="text-sm text-destructive">
                    {state.errors.email[0]}
                  </p>
                )}
              </div>
              {state.message && !state.success && (
                <Alert variant="destructive">
                  <AlertDescription>{state.message}</AlertDescription>
                </Alert>
              )}
              <div className="flex items-center justify-center">
                <Button
                  type="submit"
                  className="h-12 px-8 text-base transition-all duration-200 group w-full"
                  disabled={isPending}
                >
                  {isPending ? (
                    <>
                      <Loader2 className="mr-2 h-4 w-4 animate-spin" />
                      Subscribing...
                    </>
                  ) : (
                    <>
                      <Mail className="mr-2 h-4 w-4" />
                      Subscribe to Newsletter
                    </>
                  )}
                </Button>
              </div>
            </form>
          )}
        </div>
      </div>
    </section>
  );
}

"use client";

import { useState } from "react";
import { formatDistanceToNow } from "date-fns";
import { Card, CardContent, CardHeader } from "@/registry/new-york/ui/card";
import { Input } from "@/registry/new-york/ui/input";
import { Button } from "@/registry/new-york/ui/button";
import { Badge } from "@/registry/new-york/ui/badge";
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from "@/registry/new-york/ui/table";
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuTrigger,
} from "@/registry/new-york/ui/dropdown-menu";
import { deleteSubscriber } from "@/registry/new-york/dynamic-components/subscribe-newsletter/actions/subscribe-newsletter-actions";
import { useToast } from "@/registry/new-york/hooks/use-toast";
import { MoreVertical, Search, Trash2, AlertCircle } from "lucide-react";

type Subscriber = {
  id: string;
  email: string;
  name: string | null;
  status: string;
  createdAt: Date;
};

export function SubscriberList({
  initialSubscribers,
}: {
  initialSubscribers: Subscriber[];
}) {
  const [subscribers, setSubscribers] = useState(initialSubscribers);
  const [searchTerm, setSearchTerm] = useState("");
  const { toast } = useToast();

  const filteredSubscribers = subscribers.filter(
    (subscriber) =>
      subscriber.email.toLowerCase().includes(searchTerm.toLowerCase()) ||
      (subscriber.name &&
        subscriber.name.toLowerCase().includes(searchTerm.toLowerCase()))
  );

  const handleDelete = async (id: string) => {
    const result = await deleteSubscriber(id);

    if (result.success) {
      setSubscribers(subscribers.filter((subscriber) => subscriber.id !== id));

      toast({
        title: "Subscriber deleted",
        description: "The subscriber has been permanently deleted",
      });
    } else {
      toast({
        title: "Error",
        description: result.error || "Failed to delete subscriber",
        variant: "destructive",
      });
    }
  };

  return (
    <Card>
      <CardHeader>
        <div className="flex items-center justify-between">
          <h2 className="text-xl font-semibold">Newsletter Subscribers</h2>
          <div className="relative w-64">
            <Search className="absolute left-2 top-2.5 h-4 w-4 text-muted-foreground" />
            <Input
              placeholder="Search subscribers..."
              className="pl-8"
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
            />
          </div>
        </div>
      </CardHeader>
      <CardContent>
        {filteredSubscribers.length === 0 ? (
          <div className="flex flex-col items-center justify-center py-12 text-center">
            <AlertCircle className="h-12 w-12 text-muted-foreground mb-4" />
            <h3 className="text-lg font-medium">No subscribers found</h3>
            <p className="text-muted-foreground mt-2">
              {searchTerm
                ? "No subscribers match your search criteria."
                : "There are no subscribers yet."}
            </p>
          </div>
        ) : (
          <Table>
            <TableHeader>
              <TableRow>
                <TableHead>Email</TableHead>
                <TableHead>Name</TableHead>
                <TableHead>Status</TableHead>
                <TableHead>Subscribed On</TableHead>
                <TableHead className="text-right">Actions</TableHead>
              </TableRow>
            </TableHeader>
            <TableBody>
              {filteredSubscribers.map((subscriber) => (
                <TableRow key={subscriber.id}>
                  <TableCell className="font-medium">
                    {subscriber.email}
                  </TableCell>
                  <TableCell>{subscriber.name || "-"}</TableCell>
                  <TableCell>
                    <Badge
                      variant={
                        subscriber.status === "ACTIVE" ? "default" : "secondary"
                      }
                    >
                      {subscriber.status === "ACTIVE"
                        ? "Active"
                        : "Unsubscribed"}
                    </Badge>
                  </TableCell>
                  <TableCell>
                    {formatDistanceToNow(new Date(subscriber.createdAt), {
                      addSuffix: true,
                    })}
                  </TableCell>
                  <TableCell className="text-right">
                    <DropdownMenu>
                      <DropdownMenuTrigger asChild>
                        <Button variant="ghost" size="icon">
                          <MoreVertical className="h-4 w-4" />
                          <span className="sr-only">Actions</span>
                        </Button>
                      </DropdownMenuTrigger>
                      <DropdownMenuContent align="end">
                        <DropdownMenuItem
                          onClick={() => handleDelete(subscriber.id)}
                          className="text-destructive focus:text-destructive"
                        >
                          <Trash2 className="h-4 w-4 mr-2" />
                          Delete
                        </DropdownMenuItem>
                      </DropdownMenuContent>
                    </DropdownMenu>
                  </TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        )}
      </CardContent>
    </Card>
  );
}

"use client";

import { useSearchParams } from "next/navigation";
import { useState, useEffect } from "react";
import {
  Card,
  CardContent,
  CardDescription,
  CardHeader,
  CardTitle,
} from "@/registry/new-york/ui/card";
import { XCircle, Loader2 } from "lucide-react";
import Link from "next/link";
import { Button } from "@/registry/new-york/ui/button";
import { UnsubscribeForm } from "@/registry/new-york/dynamic-components/subscribe-newsletter/components/unsubscribe-form";
import { verifyUnsubscribeToken } from "@/registry/new-york/dynamic-components/subscribe-newsletter/actions/subscribe-newsletter-actions";

export function Unsubscribe() {
  const searchParams = useSearchParams();
  const token = searchParams.get("token");

  const [isLoading, setIsLoading] = useState(true);
  const [email, setEmail] = useState<string | null>(null);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    async function verifyToken() {
      if (!token) {
        setIsLoading(false);
        return;
      }
      try {
        const verifiedEmail = await verifyUnsubscribeToken(token);
        setEmail(verifiedEmail);
      } catch (err) {
        setError("Failed to verify token");
        console.error(err);
      } finally {
        setIsLoading(false);
      }
    }
    verifyToken();
  }, [token]);

  if (!token) {
    return (
      <Card className="max-w-md w-full">
        <CardHeader>
          <CardTitle className="text-xl">Invalid Request</CardTitle>
          <CardDescription>
            The unsubscribe link is invalid or has expired.
          </CardDescription>
        </CardHeader>
        <CardContent className="flex flex-col items-center">
          <XCircle className="h-16 w-16 text-destructive mb-4" />
          <p className="text-center mb-6">
            This link appears to be invalid or has expired. Please check the
            link and try again.
          </p>
          <Button asChild>
            <Link href="/">Return to Homepage</Link>
          </Button>
        </CardContent>
      </Card>
    );
  }

  if (isLoading) {
    return (
      <Card className="max-w-md w-full">
        <CardContent className="flex flex-col items-center py-10">
          <Loader2 className="h-16 w-16 animate-spin mb-4 text-primary" />
          <p className="text-center">Verifying your unsubscribe request...</p>
        </CardContent>
      </Card>
    );
  }

  if (email) {
    return <UnsubscribeForm token={token} email={email} />;
  }

  return (
    <Card className="max-w-md w-full">
      <CardHeader>
        <CardTitle className="text-xl">Invalid Unsubscribe Link</CardTitle>
        <CardDescription>
          The unsubscribe link is invalid or has expired.
        </CardDescription>
      </CardHeader>
      <CardContent className="flex flex-col items-center">
        <XCircle className="h-16 w-16 text-destructive mb-4" />
        <p className="text-center mb-6">
          {error ||
            "This link appears to be invalid or has expired. Please check the link and try again."}
        </p>
        <Button asChild>
          <Link href="/">Return to Homepage</Link>
        </Button>
      </CardContent>
    </Card>
  );
}

"use client";

import {
