import { Button } from "@/registry/new-york/ui/button";
import { useUnsubscribeForm } from "@/registry/new-york/dynamic-components/subscribe-newsletter/hooks/use-unsubscribe-form";

export function UnsubscribeForm({
  token,
  email,
}: {
  token: string;
  email: string;
}) {
  const { isLoading, isUnsubscribed, message, handleUnsubscribe } =
    useUnsubscribeForm(token);
  if (isUnsubscribed) {
    return (
      <Card className="max-w-md w-full">
        <CardHeader>
          <CardTitle className="text-xl">Successfully Unsubscribed</CardTitle>
          <CardDescription>
            You have been removed from our mailing list
          </CardDescription>
        </CardHeader>
        <CardContent className="flex flex-col items-center">
          <CheckCircle className="h-16 w-16 text-primary mb-4" />
          <p className="text-center mb-6">{message}</p>
          <Button asChild>
            <Link href="/">Return to Homepage</Link>
          </Button>
        </CardContent>
      </Card>
    );
  }

  return (
    <Card className="max-w-md w-full">
      <CardHeader>
        <CardTitle className="text-xl">Unsubscribe from Newsletter</CardTitle>
        <CardDescription>
          You are about to unsubscribe <strong>{email}</strong> from our
          newsletter.
        </CardDescription>
      </CardHeader>
      <CardContent className="flex flex-col items-center">
