            {isLoading ? (
              <>
                <Loader2 className="mr-2 h-4 w-4 animate-spin" />
                Processing...
              </>
            ) : (
              "Confirm Unsubscribe"
            )}
          </Button>
        </div>
      </CardContent>
    </Card>
  );
}

