"use server";

import * as z from "zod";
import crypto from "crypto";
import { sendTwoFactorTokenEmail } from "@/registry/new-york/dynamic-components/two-factor-form/lib/two-factor-utils";
import { db } from "@/registry/new-york/lib/db";

// Validation schemas
const TwoFactorRequestSchema = z.object({
  email: z.string().email("Invalid email address"),
});

const TwoFactorVerifySchema = z.object({
  email: z.string().email("Invalid email address"),
  code: z
    .string()
    .min(6, "Code must be 6 digits")
    .max(6, "Code must be 6 digits"),
});

export interface TwoFactorActionResult {
  success?: string;
  error?: string;
  twoFactorRequired?: boolean;
}

export interface TwoFactorToken {
  id: string;
  email: string;
  token: string;
  expires: Date;
}

export interface TwoFactorConfirmation {
  id: string;
  userId: string;
}
// Request 2FA code
export const requestTwoFactorCode = async (
  values: z.infer<typeof TwoFactorRequestSchema>,
): Promise<TwoFactorActionResult> => {
  try {
    const validatedFields = TwoFactorRequestSchema.safeParse(values);

    if (!validatedFields.success) {
      return { error: "Invalid email address" };
    }

    const { email } = validatedFields.data;

    // Generate and send 2FA token
    const twoFactorToken = await generateTwoFactorToken(email);
    await sendTwoFactorTokenEmail(email, twoFactorToken.token);

    return {
      success: "Verification code sent to your email",
      twoFactorRequired: true,
    };
  } catch (error) {
    console.error("2FA request error:", error);
    return { error: "Failed to send verification code" };
  }
};

// Verify 2FA code
export const verifyTwoFactorCode = async (
  values: z.infer<typeof TwoFactorVerifySchema>,
): Promise<TwoFactorActionResult> => {
  try {
    const validatedFields = TwoFactorVerifySchema.safeParse(values);

    if (!validatedFields.success) {
      return { error: "Invalid input" };
    }

    const { email, code } = validatedFields.data;

    // Get the token from database
    const twoFactorToken = await getTwoFactorTokenByEmail(email);

    if (!twoFactorToken) {
      return { error: "Invalid or expired code" };
    }

    if (twoFactorToken.token !== code) {
      return { error: "Invalid verification code" };
    }

    const hasExpired = new Date(twoFactorToken.expires) < new Date();
    if (hasExpired) {
      return { error: "Verification code has expired" };
    }

    // Clean up the token after successful verification
    await db.twoFactorToken.delete({
      where: { id: twoFactorToken.id },
    });

    return { success: "Two-factor authentication verified successfully" };
  } catch (error) {
    console.error("2FA verification error:", error);
    return { error: "Verification failed" };
  }
};

// Enable/Disable 2FA for a user
export const toggleTwoFactor = async (
  userId: string,
  enabled: boolean,
): Promise<TwoFactorActionResult> => {
  try {
    await db.user.update({
      where: { id: userId },
      data: { isTwoFactorEnabled: enabled },
    });

    if (!enabled) {
      // Remove any existing 2FA confirmations when disabling
      const existingConfirmation =
        await getTwoFactorConfirmationByUserId(userId);
      if (existingConfirmation) {
        await db.twoFactorConfirmation.delete({
          where: { id: existingConfirmation.id },
        });
      }
    }

    return {
      success: `Two-factor authentication ${enabled ? "enabled" : "disabled"} successfully`,
    };
  } catch (error) {
    return { error: "Failed to update two-factor authentication settings" };
  }
};

// Database operations using Prisma
export const getTwoFactorTokenByToken = async (
  token: string,
): Promise<TwoFactorToken | null> => {
  try {
    const twoFactorToken = await db.twoFactorToken.findUnique({
      where: { token },
    });
    return twoFactorToken;
  } catch {
    return null;
  }
};

export const getTwoFactorTokenByEmail = async (
  email: string,
): Promise<TwoFactorToken | null> => {
  try {
    const twoFactorToken = await db.twoFactorToken.findFirst({
      where: { email },
    });
    return twoFactorToken;
  } catch {
    return null;
  }
};

export const getTwoFactorConfirmationByUserId = async (
  userId: string,
): Promise<TwoFactorConfirmation | null> => {
  try {
    const twoFactorConfirmation = await db.twoFactorConfirmation.findUnique({
      where: { userId },
    });
    return twoFactorConfirmation;
  } catch {
    return null;
  }
};

export const generateTwoFactorToken = async (
  email: string,
): Promise<TwoFactorToken> => {
  const token = crypto.randomInt(100_000, 1_000_000).toString();
  const expires = new Date(new Date().getTime() + 5 * 60 * 1000); // 5 minute expiry

  // Delete existing token if any
  const existingToken = await getTwoFactorTokenByEmail(email);
  if (existingToken) {
    await db.twoFactorToken.delete({
      where: { id: existingToken.id },
    });
  }

  // Create new token
  const twoFactorToken = await db.twoFactorToken.create({
    data: {
      email,
      token,
      expires,
    },
  });

  return twoFactorToken;
};

import { PrismaClient } from "@/lib/generated/prisma/client";

declare global {
  var prisma: PrismaClient | undefined;
}
export const db = globalThis.prisma || new PrismaClient();

if (process.env.NODE_ENV !== "production") {
  globalThis.prisma = db;
}

"use client";

import { useState, useTransition } from "react";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import * as z from "zod";
import { Button } from "@/registry/new-york/ui/button";
import { Input } from "@/registry/new-york/ui/input";
import { Label } from "@/registry/new-york/ui/label";
import {
  Card,
  CardContent,
  CardDescription,
  CardHeader,
  CardTitle,
} from "@/registry/new-york/ui/card";
import { Alert, AlertDescription } from "@/registry/new-york/ui/alert";
import { Loader2, Shield, Mail } from "lucide-react";
import {
  requestTwoFactorCode,
  verifyTwoFactorCode,
} from "@/registry/new-york/dynamic-components/two-factor-form/actions/two-factor-actions";

// Validation schemas
const EmailSchema = z.object({
  email: z.string().email("Please enter a valid email address"),
});

const VerificationSchema = z.object({
  code: z
    .string()
    .min(6, "Code must be 6 digits")
    .max(6, "Code must be 6 digits"),
});

interface TwoFactorFormProps {
  onSuccess?: (email: string) => void;
  onError?: (error: string) => void;
  title?: string;
  description?: string;
  className?: string;
}

export function TwoFactorForm({
  onSuccess,
  onError,
  title = "Two-Factor Authentication",
  description = "Enter your email to receive a verification code",
  className = "",
}: TwoFactorFormProps) {
  const [step, setStep] = useState<"email" | "verification">("email");
  const [email, setEmail] = useState("");
  const [message, setMessage] = useState<{
    type: "success" | "error";
    text: string;
  } | null>(null);
  const [isPending, startTransition] = useTransition();

  const emailForm = useForm<z.infer<typeof EmailSchema>>({
    resolver: zodResolver(EmailSchema),
    defaultValues: {
      email: "",
    },
  });

  const verificationForm = useForm<z.infer<typeof VerificationSchema>>({
    resolver: zodResolver(VerificationSchema),
    defaultValues: {
      code: "",
    },
  });

  const handleEmailSubmit = (values: z.infer<typeof EmailSchema>) => {
    setMessage(null);

    startTransition(async () => {
      try {
        const result = await requestTwoFactorCode(values);

        if (result.error) {
          setMessage({ type: "error", text: result.error });
          onError?.(result.error);
        } else if (result.success) {
          setMessage({ type: "success", text: result.success });
          setEmail(values.email);
          setStep("verification");
        }
      } catch (error) {
        const errorMessage = "Something went wrong. Please try again.";
        setMessage({ type: "error", text: errorMessage });
        onError?.(errorMessage);
      }
    });
  };

  const handleVerificationSubmit = (
    values: z.infer<typeof VerificationSchema>,
  ) => {
    setMessage(null);

    startTransition(async () => {
      try {
        const result = await verifyTwoFactorCode({
          email,
          code: values.code,
        });

        if (result.error) {
          setMessage({ type: "error", text: result.error });
          onError?.(result.error);
        } else if (result.success) {
          setMessage({ type: "success", text: result.success });
          onSuccess?.(email);
        }
      } catch (error) {
        const errorMessage = "Verification failed. Please try again.";
        setMessage({ type: "error", text: errorMessage });
        onError?.(errorMessage);
      }
    });
  };

  const resetForm = () => {
    setStep("email");
    setEmail("");
    setMessage(null);
    emailForm.reset();
    verificationForm.reset();
  };

  return (
    <Card className={className}>
      <CardHeader className="text-center">
        <div className="mx-auto mb-2 flex h-12 w-12 items-center justify-center rounded-full bg-blue-100">
          <Shield className="h-6 w-6 text-blue-600" />
        </div>
        <CardTitle>{title}</CardTitle>
        <CardDescription>
          {step === "email"
            ? description
            : `Enter the 6-digit code sent to ${email}`}
        </CardDescription>
      </CardHeader>
      <CardContent className="space-y-4">
        {message && (
          <Alert variant={message.type === "error" ? "destructive" : "default"}>
            <AlertDescription>{message.text}</AlertDescription>
          </Alert>
        )}

        {step === "email" ? (
          <form
            onSubmit={emailForm.handleSubmit(handleEmailSubmit)}
            className="space-y-4"
          >
            <div className="space-y-2">
              <Label htmlFor="email">Email Address</Label>
              <div className="relative">
                <Mail className="absolute left-3 top-3 h-4 w-4 text-gray-400" />
                <Input
                  id="email"
                  type="email"
                  placeholder="Enter your email"
                  className="pl-10"
                  disabled={isPending}
                  {...emailForm.register("email")}
                />
              </div>
              {emailForm.formState.errors.email && (
                <p className="text-sm text-red-600">
                  {emailForm.formState.errors.email.message}
                </p>
              )}
            </div>
            <Button type="submit" className="w-full" disabled={isPending}>
              {isPending ? (
                <>
                  <Loader2 className="mr-2 h-4 w-4 animate-spin" />
                  Sending Code...
                </>
              ) : (
                "Send Verification Code"
              )}
            </Button>
          </form>
        ) : (
          <form
            onSubmit={verificationForm.handleSubmit(handleVerificationSubmit)}
            className="space-y-4"
          >
            <div className="space-y-2">
              <Label htmlFor="code">Verification Code</Label>
              <Input
                id="code"
                type="text"
                placeholder="Enter 6-digit code"
                maxLength={6}
                className="text-center text-lg tracking-widest"
                disabled={isPending}
                {...verificationForm.register("code")}
              />
              {verificationForm.formState.errors.code && (
                <p className="text-sm text-red-600">
                  {verificationForm.formState.errors.code.message}
                </p>
              )}
            </div>
            <div className="space-y-2">
              <Button type="submit" className="w-full" disabled={isPending}>
                {isPending ? (
                  <>
                    <Loader2 className="mr-2 h-4 w-4 animate-spin" />
                    Verifying...
                  </>
                ) : (
                  "Verify Code"
                )}
              </Button>
              <Button
                type="button"
                variant="outline"
                className="w-full"
                onClick={resetForm}
                disabled={isPending}
              >
                Use Different Email
              </Button>
            </div>
          </form>
        )}
      </CardContent>
    </Card>
  );
}

import nodemailer from "nodemailer";
import { getTwoFactorEmailTemplate } from "@/registry/new-york/dynamic-components/two-factor-form/emails/two-factor-template";

// Email sending function - Complete implementation with template
export const sendTwoFactorTokenEmail = async (
  email: string,
  token: string,
): Promise<void> => {
  try {
    // Create transporter based on environment variables
    const transporter = nodemailer.createTransport({
      host: process.env.EMAIL_SERVER_HOST,
      port: Number(process.env.EMAIL_SERVER_PORT) || 587,
      secure: Number(process.env.EMAIL_SERVER_PORT) === 465, // true for 465, false for other ports
      auth: {
        user: process.env.EMAIL_SERVER_USER,
        pass: process.env.EMAIL_SERVER_PASSWORD,
      },
    });

    // Get email templates
    const { html, text } = getTwoFactorEmailTemplate(token);

    // Send email
    await transporter.sendMail({
      from: `${process.env.EMAIL_FROM || "AxionJS Team"}`,
      to: email,
      subject: "🔐 Your Two-Factor Authentication Code",
      text,
      html,
    });

    console.log(`2FA code sent successfully to ${email}`);
  } catch (error) {
    console.error("Failed to send 2FA email:", error);
    throw new Error("Failed to send verification email");
  }
};

export const getTwoFactorEmailTemplate = (token: string) => {
  const html = `
      <!DOCTYPE html>
      <html>
        <head>
          <meta charset="utf-8">
          <title>Two-Factor Authentication Code</title>
          <style>
            body { 
              font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, sans-serif; 
              line-height: 1.6; 
              color: #333; 
              margin: 0; 
              padding: 0; 
              background-color: #f5f5f5; 
            }
            .container { 
              max-width: 600px; 
              margin: 40px auto; 
              background: white; 
              border-radius: 12px; 
              overflow: hidden; 
              box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1); 
            }
            .header { 
              background: linear-gradient(135deg, #667eea 0%, #764ba2 100%); 
              color: white; 
              padding: 40px 30px; 
              text-align: center; 
            }
            .header h1 { 
              margin: 0; 
              font-size: 28px; 
              font-weight: 600; 
            }
            .content { 
              padding: 40px 30px; 
            }
            .code-container { 
              text-align: center; 
              margin: 30px 0; 
            }
            .code { 
              display: inline-block;
              font-size: 36px; 
              font-weight: bold; 
              padding: 20px 30px; 
              background: #f8f9fa; 
              border: 2px dashed #dee2e6;
              border-radius: 12px; 
              letter-spacing: 8px; 
              color: #495057;
              font-family: 'Courier New', monospace;
            }
            .info { 
              background: #e3f2fd; 
              border-left: 4px solid #2196f3; 
              padding: 15px 20px; 
              margin: 25px 0; 
              border-radius: 4px; 
            }
            .warning { 
              background: #fff3e0; 
              border-left: 4px solid #ff9800; 
              padding: 15px 20px; 
              margin: 25px 0; 
              border-radius: 4px; 
            }
            .footer { 
              background: #f8f9fa; 
              padding: 20px 30px; 
              text-align: center; 
              font-size: 14px; 
              color: #6c757d; 
              border-top: 1px solid #e9ecef; 
            }
            .btn {
              display: inline-block;
              padding: 12px 24px;
              background: #007bff;
              color: white;
              text-decoration: none;
              border-radius: 6px;
              font-weight: 500;
              margin: 20px 0;
            }
          </style>
        </head>
        <body>
          <div class="container">
            <div class="header">
              <h1>🔐 Two-Factor Authentication</h1>
              <p style="margin: 10px 0 0 0; opacity: 0.9;">Secure your account with this verification code</p>
            </div>
            <div class="content">
              <h2 style="color: #333; margin-top: 0;">Your Verification Code</h2>
              <p>Enter the following code to complete your two-factor authentication:</p>
              
              <div class="code-container">
                <div class="code">${token}</div>
              </div>
              
              <div class="info">
                <strong>⏰ Time Sensitive:</strong> This code will expire in <strong>5 minutes</strong> for your security.
              </div>
              
              <div class="warning">
                <strong>🛡️ Security Notice:</strong> If you didn't request this code, please ignore this email and consider changing your password. Never share this code with anyone.
              </div>
              
              <p style="margin-top: 30px;">
                Having trouble? Make sure you're entering the code exactly as shown above, including any spaces or dashes.
              </p>
            </div>
            <div class="footer">
              <p>This is an automated security message from your application.</p>
              <p>Please do not reply to this email.</p>
            </div>
          </div>
        </body>
      </html>
    `;

  const text = `
  Two-Factor Authentication Code
  
  Your verification code is: ${token}
  
  This code will expire in 5 minutes for security reasons.
  
  Security Notice: If you didn't request this code, please ignore this email and consider changing your password. Never share this code with anyone.
  
  Having trouble? Make sure you're entering the code exactly as shown above.
  
  This is an automated security message. Please do not reply to this email.
    `;

  return { html, text };
};

"use client";
import { TwoFactorForm } from "@/registry/new-york/dynamic-components/two-factor-form/components/two-factor-form";

export default function TwoFA() {
  return (
    <div className="min-h-screen bg-gray-50 py-12">
      <div className="mx-auto max-w-xl px-4">
        <div className="grid gap-8">
          <div className="space-y-4">
            <h2 className="text-xl font-semibold text-gray-800">
              Two-Factor Authentication
            </h2>
            <TwoFactorForm
              onSuccess={(email) => {
                console.log("2FA Success for:", email);
              }}
              onError={(error) => {}}
            />
          </div>
        </div>
      </div>
    </div>
  );
}
