"use server";

import { db } from "@/registry/new-york/lib/db";
import { revalidatePath } from "next/cache";

import { z } from "zod";

const inventoryFormSchema = z.object({
  name: z.string().min(2),
  category: z.string().min(1),
  price: z.number().positive(),
  stock: z.number().int().nonnegative(),
  sku: z.string().min(3),
});

export async function getInventoryItems({
  page = 1,
  pageSize = 10,
  sortBy = "updatedAt",
  sortOrder = "desc",
  filters = {},
  lowStockOnly = false,
}: {
  page?: number;
  pageSize?: number;
  sortBy?: string;
  sortOrder?: "asc" | "desc";
  filters?: Record<string, any>;
  lowStockOnly?: boolean;
} = {}) {
  try {
    // Calculate pagination values
    const skip = (page - 1) * pageSize;
    const take = pageSize;

    // Build where clause based on filters
    const where: any = {};

    if (filters.name) {
      where.name = {
        contains: filters.name,
        mode: "insensitive",
      };
    }

    if (filters.category && filters.category !== "") {
      where.category = filters.category;
    }

    // Add low stock filter if requested
    if (lowStockOnly) {
      where.stock = {
        lt: 10, // Items with stock less than 10
      };
    }

    // Get paginated items with sorting
    const items = await db.inventoryItem.findMany({
      where,
      orderBy: {
        [sortBy]: sortOrder,
      },
      skip,
      take,
    });

    // Get total count for pagination
    const totalItems = await db.inventoryItem.count({ where });
    const totalPages = Math.ceil(totalItems / pageSize);

    // Get count of low stock items for the alert
    const lowStockCount = await db.inventoryItem.count({
      where: {
        stock: {
          lt: 10,
        },
      },
    });

    return {
      items,
      meta: {
        currentPage: page,
        pageSize,
        totalItems,
        totalPages,
        lowStockCount,
      },
    };
  } catch (error) {
    console.error("Failed to fetch inventory items:", error);
    throw new Error("Failed to fetch inventory items");
  }
}

export async function getInventoryItem(id: string) {
  try {
    const item = await db.inventoryItem.findUnique({
      where: { id },
    });

    if (!item) {
      throw new Error("Inventory item not found");
    }

    return item;
  } catch (error) {
    console.error(`Failed to fetch inventory item ${id}:`, error);
    throw new Error("Failed to fetch inventory item");
  }
}

export async function createInventoryItem(
  values: z.infer<typeof inventoryFormSchema>,
) {
  try {
    const validatedFields = inventoryFormSchema.parse(values);

    // Check if SKU already exists
    const existingItem = await db.inventoryItem.findUnique({
      where: { sku: validatedFields.sku },
    });
    if (existingItem) {
      return { error: "SKU already exists" };
    }

    await db.inventoryItem.create({
      data: {
        name: validatedFields.name,
        category: validatedFields.category,
        price: validatedFields.price,
        stock: validatedFields.stock,
        sku: validatedFields.sku,
      },
    });

    revalidatePath("/inventory");
    return { success: true };
  } catch (error) {
    console.error("Failed to create inventory item:", error);
    return { error: "Failed to create inventory item" };
  }
}

export async function updateInventoryItem(
  id: string,
  values: z.infer<typeof inventoryFormSchema>,
) {
  try {
    const validatedFields = inventoryFormSchema.parse(values);

    //Check if same sku exists
    const existingItem = await db.inventoryItem.findUnique({
      where: { sku: validatedFields.sku },
    });
    if (existingItem && existingItem.id !== id) {
      return { error: "SKU already exists" };
    }

    // Update the inventory item
    await db.inventoryItem.update({
      where: { id },
      data: {
        name: validatedFields.name,
        category: validatedFields.category,
        price: validatedFields.price,
        stock: validatedFields.stock,
        sku: validatedFields.sku,
      },
    });

    revalidatePath("/inventory");
    return { success: true };
  } catch (error) {
    console.error(`Failed to update inventory item ${id}:`, error);
    return { error: "Failed to update inventory item" };
  }
}

export async function deleteInventoryItem(id: string) {
  try {
    await db.inventoryItem.delete({
      where: { id },
    });

    revalidatePath("/inventory");
    return { success: true };
  } catch (error) {
    console.error(`Failed to delete inventory item ${id}:`, error);
    return { error: "Failed to delete inventory item" };
  }
}

import { PrismaClient } from "@prisma/client";

declare global {
  var prisma: PrismaClient | undefined;
}
export const db = globalThis.prisma || new PrismaClient();

if (process.env.NODE_ENV !== "production") {
  globalThis.prisma = db;
}

"use client";

import type React from "react";
import { useState } from "react";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import type { InventoryItem } from "@prisma/client";
import { Plus } from "lucide-react";
import { Button } from "@/components/ui/button";
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogFooter,
  DialogHeader,
  DialogTitle,
  DialogTrigger,
} from "@/registry/new-york/ui/dialog";
import {
  Form,
  FormControl,
  FormField,
  FormItem,
  FormLabel,
  FormMessage,
} from "@/registry/new-york/ui/form";
import { Input } from "@/components/ui/input";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/registry/new-york/ui/select";
import {
  createInventoryItem,
  updateInventoryItem,
} from "@/registry/new-york/dynamic-components/inventory-manager/actions/inventory";
import { useToast } from "@/registry/new-york/hooks/use-toast";
import { useInventory } from "@/registry/new-york/dynamic-components/inventory-manager/lib/context";

const formSchema = z.object({
  name: z.string().min(2, {
    message: "Name must be at least 2 characters.",
  }),
  category: z.string().min(1, {
    message: "Please select a category.",
  }),
  price: z.coerce.number().positive({
    message: "Price must be a positive number.",
  }),
  stock: z.coerce.number().int().nonnegative({
    message: "Stock must be a non-negative integer.",
  }),
  sku: z.string().min(3, {
    message: "SKU must be at least 3 characters.",
  }),
});

type FormValues = z.infer<typeof formSchema>;

interface InventoryFormProps {
  item?: InventoryItem;
  onSuccess?: () => void;
}

export function InventoryForm({ item, onSuccess }: InventoryFormProps) {
  const { toast } = useToast();
  const { refreshData } = useInventory();
  const [isSubmitting, setIsSubmitting] = useState(false);
  const isEditing = !!item;

  const form = useForm<FormValues>({
    resolver: zodResolver(formSchema),
    defaultValues: {
      name: item?.name || "",
      category: item?.category || "",
      price: item?.price || 0,
      stock: item?.stock || 0,
      sku: item?.sku || "",
    },
  });

  async function onSubmit(values: FormValues) {
    try {
      setIsSubmitting(true);
      if (isEditing && item) {
        const response = await updateInventoryItem(item.id, values);
        if (response.error) {
          toast({
            title: "Error",
            description: response.error,
            variant: "destructive",
          });
        } else {
          toast({
            title: "Item updated",
            description: "The inventory item has been updated successfully.",
          });
        }
      } else {
        const response = await createInventoryItem(values);
        if (response.error) {
          toast({
            title: "Error",
            description: response.error,
            variant: "destructive",
          });
        } else {
          toast({
            title: "Item created",
            description: "The new inventory item has been added successfully.",
          });
        }
      }
      form.reset();
      onSuccess?.();
      await refreshData();
    } catch (error) {
      console.error(error);
      toast({
        title: "Error",
        description: "Failed to save inventory item. Please try again.",
        variant: "destructive",
      });
    } finally {
      setIsSubmitting(false);
    }
  }

  return (
    <Form {...form}>
      <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-6">
        <FormField
          control={form.control}
          name="name"
          render={({ field }) => (
            <FormItem>
              <FormLabel>Name</FormLabel>
              <FormControl>
                <Input placeholder="Item name" {...field} />
              </FormControl>
              <FormMessage />
            </FormItem>
          )}
        />

        <FormField
          control={form.control}
          name="category"
          render={({ field }) => (
            <FormItem>
              <FormLabel>Category</FormLabel>
              <Select onValueChange={field.onChange} defaultValue={field.value}>
                <FormControl>
                  <SelectTrigger>
                    <SelectValue placeholder="Select a category" />
                  </SelectTrigger>
                </FormControl>
                <SelectContent>
                  <SelectItem value="Electronics">Electronics</SelectItem>
                  <SelectItem value="Clothing">Clothing</SelectItem>
                  <SelectItem value="Food">Food</SelectItem>
                  <SelectItem value="Other">Other</SelectItem>
                </SelectContent>
              </Select>
              <FormMessage />
            </FormItem>
          )}
        />

        <div className="grid grid-cols-2 gap-4">
          <FormField
            control={form.control}
            name="price"
            render={({ field }) => (
              <FormItem>
                <FormLabel>Price</FormLabel>
                <FormControl>
                  <Input type="number" step="0.01" min="0" {...field} />
                </FormControl>
                <FormMessage />
              </FormItem>
            )}
          />

          <FormField
            control={form.control}
            name="stock"
            render={({ field }) => (
              <FormItem>
                <FormLabel>Stock</FormLabel>
                <FormControl>
                  <Input type="number" min="0" {...field} />
                </FormControl>
                <FormMessage />
              </FormItem>
            )}
          />
        </div>

        <FormField
          control={form.control}
          name="sku"
          render={({ field }) => (
            <FormItem>
              <FormLabel>SKU</FormLabel>
              <FormControl>
                <Input placeholder="SKU000123" {...field} />
              </FormControl>
              <FormMessage />
            </FormItem>
          )}
        />

        <DialogFooter>
          <Button type="submit" disabled={isSubmitting}>
            {isSubmitting ? (
              <>
                <span className="animate-spin mr-2">‚ü≥</span>
                {isEditing ? "Updating..." : "Creating..."}
              </>
            ) : (
              <>{isEditing ? "Update" : "Create"} Item</>
            )}
          </Button>
        </DialogFooter>
      </form>
    </Form>
  );
}

// Create Item Dialog
export function CreateInventoryItem() {
  const [open, setOpen] = useState(false);

  return (
    <Dialog open={open} onOpenChange={setOpen}>
      <DialogTrigger asChild>
        <Button>
          <Plus className="mr-2 h-4 w-4" />
          Add Item
        </Button>
      </DialogTrigger>
      <DialogContent className="sm:max-w-[425px]">
        <DialogHeader>
          <DialogTitle>Add Inventory Item</DialogTitle>
          <DialogDescription>
            Create a new item in your inventory. Click save when you're done.
          </DialogDescription>
        </DialogHeader>
        <InventoryForm onSuccess={() => setOpen(false)} />
      </DialogContent>
    </Dialog>
  );
}

// Edit Item Dialog
interface EditInventoryItemProps {
  item: InventoryItem;
  trigger: React.ReactNode;
}

export function EditInventoryItem({ item, trigger }: EditInventoryItemProps) {
  const [open, setOpen] = useState(false);

  return (
    <Dialog open={open} onOpenChange={setOpen}>
      <DialogTrigger asChild>{trigger}</DialogTrigger>
      <DialogContent className="sm:max-w-[425px]">
        <DialogHeader>
          <DialogTitle>Edit Inventory Item</DialogTitle>
          <DialogDescription>
            Make changes to your inventory item. Click save when you're done.
          </DialogDescription>
        </DialogHeader>
        <InventoryForm item={item} onSuccess={() => setOpen(false)} />
      </DialogContent>
    </Dialog>
  );
}

"use client";

import {
  Alert,
  AlertDescription,
  AlertTitle,
} from "@/registry/new-york/ui/alert";
import { AlertTriangle, AlertCircle } from "lucide-react";
import { Input } from "@/components/ui/input";

import { InventoryContext } from "@/registry/new-york/dynamic-components/inventory-manager/lib/context";
import { Button } from "@/registry/new-york/ui/button";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/registry/new-york/ui/select";
import { DataTable } from "./data-table";
import { useInventoryDataTable } from "@/registry/new-york/dynamic-components/inventory-manager/hooks/use-inventory-data-table";

export function InventoryDataTable() {
  const {
    data,
    pageCount,
    totalItems,
    lowStockCount,
    isLoading,
    isPending,
    columns,
    pageIndex,
    pageSize,
    nameFilter,
    categoryFilter,
    lowStockOnly,
    handlePaginationChange,
    handleNameFilterChange,
    handleCategoryFilterChange,
    toggleLowStockFilter,
    contextValue,
    refreshData,
  } = useInventoryDataTable();
  return (
    <InventoryContext.Provider value={contextValue}>
      <div>
        {lowStockCount > 0 && (
          <Alert
            variant="destructive"
            className="mb-4 cursor-pointer"
            onClick={toggleLowStockFilter}
          >
            <AlertCircle className="h-4 w-4" />
            <AlertTitle>Low Stock Alert</AlertTitle>
            <AlertDescription className="flex justify-between items-center">
              <span>
                {lowStockCount} {lowStockCount === 1 ? "item" : "items"} in your
                inventory {lowStockCount === 1 ? "is" : "are"} running low on
                stock.
              </span>
              <Button
                variant="destructive"
                size="sm"
                className="ml-2 border-primary text-secondary hover:text-white hover:bg-red-600"
              >
                {lowStockOnly ? "Show All Items" : "View Low Stock Items"}
              </Button>
            </AlertDescription>
          </Alert>
        )}

        {lowStockOnly && (
          <div className="bg-muted p-2 rounded-md mb-4 flex justify-between items-center">
            <div className="flex items-center">
              <AlertTriangle className="h-4 w-4 mr-2 text-destructive" />
              <span>Showing low stock items only</span>
            </div>
            <Button variant="outline" size="sm" onClick={toggleLowStockFilter}>
              Show All Items
            </Button>
          </div>
        )}

        <div className="flex items-center py-4 gap-2">
          <div className="flex-1">
            <Input
              placeholder="Filter by name..."
              value={nameFilter}
              onChange={(e) => handleNameFilterChange(e.target.value)}
              className="max-w-sm"
            />
          </div>
          <div className="w-[180px]">
            <Select
              value={categoryFilter || "all"}
              onValueChange={handleCategoryFilterChange}
            >
              <SelectTrigger>
                <SelectValue placeholder="Category" />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="all">All Categories</SelectItem>
                <SelectItem value="Electronics">Electronics</SelectItem>
                <SelectItem value="Clothing">Clothing</SelectItem>
                <SelectItem value="Food">Food</SelectItem>
                <SelectItem value="Other">Other</SelectItem>
              </SelectContent>
            </Select>
          </div>
        </div>

        {isLoading ? (
          <div className="h-[400px] w-full flex items-center justify-center">
            <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-gray-900"></div>
          </div>
        ) : (
          <DataTable
            columns={columns}
            data={data}
            pageCount={pageCount}
            pageSize={pageSize}
            pageIndex={pageIndex}
            totalItems={totalItems}
            onPaginationChange={handlePaginationChange}
            onRefresh={refreshData}
            isRefreshing={isPending}
          />
        )}
      </div>
    </InventoryContext.Provider>
  );
}

"use client";

import {
  type ColumnDef,
  flexRender,
  getCoreRowModel,
  useReactTable,
} from "@tanstack/react-table";

import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from "@/registry/new-york/ui/table";
import { Button } from "@/registry/new-york/ui/button";

import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/registry/new-york/ui/select";
import { RotateCcw } from "lucide-react";

interface DataTableProps<TData, TValue> {
  columns: ColumnDef<TData, TValue>[];
  data: TData[];
  pageCount: number;
  pageSize: number;
  pageIndex: number;
  totalItems: number;
  onPaginationChange: (page: number, pageSize: number) => void;
  onRefresh: () => void;
  isRefreshing: boolean;
}

export function DataTable<TData, TValue>({
  columns,
  data,
  pageCount,
  pageSize,
  pageIndex,
  totalItems,
  onPaginationChange,
  onRefresh,
  isRefreshing,
}: DataTableProps<TData, TValue>) {
  const table = useReactTable({
    data,
    columns,
    getCoreRowModel: getCoreRowModel(),
    manualPagination: true,
    pageCount,
    state: {
      pagination: {
        pageIndex,
        pageSize,
      },
    },
  });

  // Calculate the range of items being displayed
  const startItem = pageIndex * pageSize + 1;
  const endItem = Math.min((pageIndex + 1) * pageSize, totalItems);

  return (
    <div>
      <div className="rounded-md border">
        <Table>
          <TableHeader>
            {table.getHeaderGroups().map((headerGroup) => (
              <TableRow key={headerGroup.id}>
                {headerGroup.headers.map((header) => {
                  return (
                    <TableHead key={header.id}>
                      {header.isPlaceholder
                        ? null
                        : flexRender(
                            header.column.columnDef.header,
                            header.getContext(),
                          )}
                    </TableHead>
                  );
                })}
              </TableRow>
            ))}
          </TableHeader>
          <TableBody>
            {table.getRowModel().rows.length > 0 ? (
              table.getRowModel().rows.map((row) => (
                <TableRow key={row.id}>
                  {row.getVisibleCells().map((cell) => (
                    <TableCell key={cell.id}>
                      {flexRender(
                        cell.column.columnDef.cell,
                        cell.getContext(),
                      )}
                    </TableCell>
                  ))}
                </TableRow>
              ))
            ) : (
              <TableRow>
                <TableCell
                  colSpan={columns.length}
                  className="h-24 text-center"
                >
                  No results.
                </TableCell>
              </TableRow>
            )}
          </TableBody>
        </Table>
      </div>
      <div className="flex items-center justify-between space-x-2 py-4">
        <div className="flex items-center gap-2">
          <div className="text-sm text-muted-foreground">
            Showing <span className="font-medium">{startItem}</span> to{" "}
            <span className="font-medium">{endItem}</span> of{" "}
            <span className="font-medium">{totalItems}</span> items
          </div>
          <Button
            variant="ghost"
            size="icon"
            onClick={onRefresh}
            disabled={isRefreshing}
            title="Refresh data"
          >
            <RotateCcw
              className={`h-4 w-4 ${isRefreshing ? "animate-spin" : ""}`}
            />
            <span className="sr-only">Refresh</span>
          </Button>
        </div>
        <div className="flex items-center space-x-2">
          <Button
            variant="outline"
            size="sm"
            onClick={() => onPaginationChange(pageIndex - 1, pageSize)}
            disabled={pageIndex === 0}
          >
            Previous
          </Button>
          <Button
            variant="outline"
            size="sm"
            onClick={() => onPaginationChange(pageIndex + 1, pageSize)}
            disabled={pageIndex === pageCount - 1 || pageCount === 0}
          >
            Next
          </Button>
          <Select
            value={`${pageSize}`}
            onValueChange={(value) => onPaginationChange(0, Number(value))}
          >
            <SelectTrigger className="w-[80px]">
              <SelectValue placeholder={pageSize} />
            </SelectTrigger>
            <SelectContent>
              {[5, 10, 20, 30, 40, 50].map((size) => (
                <SelectItem key={size} value={`${size}`}>
                  {size}
                </SelectItem>
              ))}
            </SelectContent>
          </Select>
        </div>
      </div>
    </div>
  );
}

"use client";

import type { ColumnDef } from "@tanstack/react-table";
import { MoreHorizontal, AlertTriangle, ArrowUpDown } from "lucide-react";
import { Button } from "@/registry/new-york/ui/button";
import { Badge } from "@/registry/new-york/ui/badge";
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuLabel,
  DropdownMenuTrigger,
} from "@/registry/new-york/ui/dropdown-menu";
import { EditInventoryItem } from "./inventory-form";
import type { InventoryItem } from "@prisma/client";

import { useState } from "react";
import { useToast } from "@/registry/new-york/hooks/use-toast";
import {
  AlertDialog,
  AlertDialogAction,
  AlertDialogCancel,
  AlertDialogContent,
  AlertDialogDescription,
  AlertDialogFooter,
  AlertDialogHeader,
  AlertDialogTitle,
} from "@/registry/new-york/ui/alert-dialog";
import { deleteInventoryItem } from "@/registry/new-york/dynamic-components/inventory-manager/actions/inventory";
import { useInventory } from "@/registry/new-york/dynamic-components/inventory-manager/lib/context";

interface ColumnProps {
  onSort: (column: string) => void;
}

export const createColumns = ({
  onSort,
}: ColumnProps): ColumnDef<InventoryItem>[] => [
  {
    accessorKey: "name",
    header: ({ column }) => {
      return (
        <Button
          variant="ghost"
          onClick={() => onSort("name")}
          className="-ml-4 h-8 data-[state=open]:bg-accent"
        >
          Name
          <ArrowUpDown className="ml-2 h-4 w-4" />
        </Button>
      );
    },
  },
  {
    accessorKey: "category",
    header: ({ column }) => {
      return (
        <Button
          variant="ghost"
          onClick={() => onSort("category")}
          className="-ml-4 h-8 data-[state=open]:bg-accent"
        >
          Category
          <ArrowUpDown className="ml-2 h-4 w-4" />
        </Button>
      );
    },
    cell: ({ row }) => {
      const category = row.getValue("category") as string;
      return <Badge variant="outline">{category}</Badge>;
    },
  },
  {
    accessorKey: "sku",
    header: ({ column }) => {
      return (
        <Button
          variant="ghost"
          onClick={() => onSort("sku")}
          className="-ml-4 h-8 data-[state=open]:bg-accent"
        >
          SKU
          <ArrowUpDown className="ml-2 h-4 w-4" />
        </Button>
      );
    },
  },
  {
    accessorKey: "price",
    header: ({ column }) => {
      return (
        <Button
          variant="ghost"
          onClick={() => onSort("price")}
          className="-ml-4 h-8 data-[state=open]:bg-accent"
        >
          Price
          <ArrowUpDown className="ml-2 h-4 w-4" />
        </Button>
      );
    },
    cell: ({ row }) => {
      const price = Number.parseFloat(row.getValue("price"));
      const formatted = new Intl.NumberFormat("en-US", {
        style: "currency",
        currency: "USD",
      }).format(price);
      return formatted;
    },
  },
  {
    accessorKey: "stock",
    header: ({ column }) => {
      return (
        <Button
          variant="ghost"
          onClick={() => onSort("stock")}
          className="-ml-4 h-8 data-[state=open]:bg-accent"
        >
          Stock
          <ArrowUpDown className="ml-2 h-4 w-4" />
        </Button>
      );
    },
    cell: ({ row }) => {
      const stock = Number.parseInt(row.getValue("stock"));

      return (
        <div className="flex items-center gap-2">
          <span className={stock < 10 ? "font-bold text-red-500" : ""}>
            {stock}
          </span>
          {stock < 10 && (
            <Badge variant="destructive" className="whitespace-nowrap">
              <AlertTriangle className="h-3 w-3 mr-1" />
              Low Stock
            </Badge>
          )}
        </div>
      );
    },
  },
  {
    id: "actions",
    cell: ({ row }) => {
      const item = row.original;

      return <ActionCell item={item} />;
    },
  },
];

function ActionCell({ item }: { item: InventoryItem }) {
  const [isDeleteAlertOpen, setIsDeleteAlertOpen] = useState(false);
  const [isDeleting, setIsDeleting] = useState(false);
  const { toast } = useToast();
  const { refreshData } = useInventory();

  const handleDelete = async () => {
    try {
      setIsDeleting(true);
      const response = await deleteInventoryItem(item.id);
      //if response gets an error, show error toast
      if (response.error) {
        toast({
          title: "Error",
          description: response.error,
          variant: "destructive",
        });
      } else {
        toast({
          title: "Item deleted",
          description: `${item.name} has been removed from inventory.`,
        });
        // Refresh data instead of reloading the page
        await refreshData();
      }
    } catch (error) {
      toast({
        title: "Error",
        description: "Failed to delete item. Please try again.",
        variant: "destructive",
      });
    } finally {
      setIsDeleting(false);
      setIsDeleteAlertOpen(false);
    }
  };

  return (
    <>
      <DropdownMenu>
        <DropdownMenuTrigger asChild>
          <Button variant="ghost" className="h-8 w-8 p-0">
            <span className="sr-only">Open menu</span>
            <MoreHorizontal className="h-4 w-4" />
          </Button>
        </DropdownMenuTrigger>
        <DropdownMenuContent align="end">
          <DropdownMenuLabel>Actions</DropdownMenuLabel>
          <EditInventoryItem
            item={item}
            trigger={
              <DropdownMenuItem onSelect={(e) => e.preventDefault()}>
                Edit
              </DropdownMenuItem>
            }
          />
          <DropdownMenuItem onClick={() => setIsDeleteAlertOpen(true)}>
            Delete
          </DropdownMenuItem>
        </DropdownMenuContent>
      </DropdownMenu>

      <AlertDialog open={isDeleteAlertOpen} onOpenChange={setIsDeleteAlertOpen}>
        <AlertDialogContent>
          <AlertDialogHeader>
            <AlertDialogTitle>Are you absolutely sure?</AlertDialogTitle>
            <AlertDialogDescription>
              This will permanently delete {item.name} from your inventory. This
              action cannot be undone.
            </AlertDialogDescription>
          </AlertDialogHeader>
          <AlertDialogFooter>
            <AlertDialogCancel disabled={isDeleting}>Cancel</AlertDialogCancel>
            <AlertDialogAction
              onClick={(e) => {
                e.preventDefault();
                handleDelete();
              }}
              disabled={isDeleting}
              className={isDeleting ? "opacity-50 cursor-not-allowed" : ""}
            >
              {isDeleting ? "Deleting..." : "Delete"}
            </AlertDialogAction>
          </AlertDialogFooter>
        </AlertDialogContent>
      </AlertDialog>
    </>
  );
}

import { createContext, useContext } from "react";

interface InventoryContextType {
  refreshData: () => Promise<void>;
  isRefreshing: boolean;
}

export const InventoryContext = createContext<InventoryContextType>({
  refreshData: async () => {},
  isRefreshing: false,
});

export const useInventory = () => useContext(InventoryContext);

import { useState, useEffect, useTransition } from "react";
import { useRouter, usePathname, useSearchParams } from "next/navigation";
import type { InventoryItem } from "@prisma/client";
import { getInventoryItems } from "@/registry/new-york/dynamic-components/inventory-manager/actions/inventory";
import { createColumns } from "@/registry/new-york/dynamic-components/inventory-manager/components/columns";

export const useInventoryDataTable = () => {
  const [data, setData] = useState<InventoryItem[]>([]);
  const [pageCount, setPageCount] = useState(0);
  const [totalItems, setTotalItems] = useState(0);
  const [lowStockCount, setLowStockCount] = useState(0);
  const [isLoading, setIsLoading] = useState(true);
  const [isPending, startTransition] = useTransition();

  // Filters
  const [nameFilter, setNameFilter] = useState("");
  const [categoryFilter, setCategoryFilter] = useState("");
  const [lowStockOnly, setLowStockOnly] = useState(false);

  // Sorting
  const [sortBy, setSortBy] = useState("updatedAt");
  const [sortOrder, setSortOrder] = useState<"asc" | "desc">("desc");

  // Pagination state
  const [pageIndex, setPageIndex] = useState(0);
  const [pageSize, setPageSize] = useState(10);

  // For URL state management
  const router = useRouter();
  const pathname = usePathname();
  const searchParams = useSearchParams();

  // Handle sorting
  const handleSort = (column: string) => {
    // If clicking the same column, toggle sort order
    if (sortBy === column) {
      const newOrder = sortOrder === "asc" ? "desc" : "asc";
      setSortOrder(newOrder);

      // Update URL params
      const params = new URLSearchParams(searchParams);
      params.set("sort", column);
      params.set("order", newOrder);
      router.push(`${pathname}?${params.toString()}`);
    } else {
      // New column, set as default desc
      setSortBy(column);
      setSortOrder("desc");

      // Update URL params
      const params = new URLSearchParams(searchParams);
      params.set("sort", column);
      params.set("order", "desc");
      router.push(`${pathname}?${params.toString()}`);
    }
  };

  // Load data based on current pagination, filters, and sorting
  const loadData = async () => {
    setIsLoading(true);
    try {
      const result = await getInventoryItems({
        page: pageIndex + 1, // API uses 1-based indexing
        pageSize,
        sortBy,
        sortOrder,
        filters: {
          name: nameFilter,
          category: categoryFilter,
        },
        lowStockOnly,
      });

      setData(result.items);
      setPageCount(result.meta.totalPages);
      setTotalItems(result.meta.totalItems);
      setLowStockCount(result.meta.lowStockCount);
    } catch (error) {
      console.error("Failed to load inventory data:", error);
    } finally {
      setIsLoading(false);
    }
  };

  // Function to refresh data without changing state
  const refreshData = async () => {
    startTransition(async () => {
      try {
        const result = await getInventoryItems({
          page: pageIndex + 1,
          pageSize,
          sortBy,
          sortOrder,
          filters: {
            name: nameFilter,
            category: categoryFilter,
          },
          lowStockOnly,
        });

        setData(result.items);
        setPageCount(result.meta.totalPages);
        setTotalItems(result.meta.totalItems);
        setLowStockCount(result.meta.lowStockCount);
      } catch (error) {
        console.error("Failed to refresh inventory data:", error);
      }
    });
  };

  // Handle pagination changes
  const handlePaginationChange = (
    newPageIndex: number,
    newPageSize: number,
  ) => {
    setPageIndex(newPageIndex);
    setPageSize(newPageSize);

    // Update URL params
    const params = new URLSearchParams(searchParams);
    params.set("page", String(newPageIndex + 1));
    params.set("pageSize", String(newPageSize));
    router.push(`${pathname}?${params.toString()}`);
  };

  // Handle filter changes
  const handleNameFilterChange = (value: string) => {
    setNameFilter(value);
    setPageIndex(0); // Reset to first page when filtering

    // Update URL params
    const params = new URLSearchParams(searchParams);
    params.set("name", value);
    params.set("page", "1");
    router.push(`${pathname}?${params.toString()}`);
  };

  const handleCategoryFilterChange = (value: string) => {
    setCategoryFilter(value === "all" ? "" : value);
    setPageIndex(0); // Reset to first page when filtering

    // Update URL params
    const params = new URLSearchParams(searchParams);
    if (value !== "all") {
      params.set("category", value);
    } else {
      params.delete("category");
    }
    params.set("page", "1");
    router.push(`${pathname}?${params.toString()}`);
  };

  // Handle low stock filter
  const toggleLowStockFilter = () => {
    const newValue = !lowStockOnly;
    setLowStockOnly(newValue);
    setPageIndex(0); // Reset to first page when filtering

    // Update URL params
    const params = new URLSearchParams(searchParams);
    if (newValue) {
      params.set("lowStock", "true");
    } else {
      params.delete("lowStock");
    }
    params.set("page", "1");
    router.push(`${pathname}?${params.toString()}`);
  };

  // Initialize from URL params on first load
  useEffect(() => {
    const page = searchParams.get("page")
      ? Number(searchParams.get("page")) - 1
      : 0;
    const size = searchParams.get("pageSize")
      ? Number(searchParams.get("pageSize"))
      : 10;
    const name = searchParams.get("name") || "";
    const category = searchParams.get("category") || "";
    const sort = searchParams.get("sort") || "updatedAt";
    const order = searchParams.get("order") === "asc" ? "asc" : "desc";
    const lowStock = searchParams.get("lowStock") === "true";

    setPageIndex(page);
    setPageSize(size);
    setNameFilter(name);
    setCategoryFilter(category);
    setSortBy(sort);
    setSortOrder(order);
    setLowStockOnly(lowStock);
  }, [searchParams]);

  // Load data when pagination, filters, or sorting change
  useEffect(() => {
    loadData();
  }, [
    pageIndex,
    pageSize,
    nameFilter,
    categoryFilter,
    sortBy,
    sortOrder,
    lowStockOnly,
  ]);

  // Create context value
  const contextValue = {
    refreshData,
    isRefreshing: isPending,
  };

  // Create columns with sort handler
  const columns = createColumns({ onSort: handleSort });

  return {
    data,
    pageCount,
    totalItems,
    lowStockCount,
    isLoading,
    isPending,
    columns,
    pageIndex,
    pageSize,
    nameFilter,
    categoryFilter,
    lowStockOnly,
    handlePaginationChange,
    handleNameFilterChange,
    handleCategoryFilterChange,
    toggleLowStockFilter,
    contextValue,
    refreshData,
  };
};
