/* Theme: comicBubble */
@theme reference {
  --color-comicBubble-background: 0 0% 100%;
  --color-comicBubble-foreground: 0 0% 3.9%;
  --color-comicBubble-card: 0 0% 100%;
  --color-comicBubble-card-foreground: 0 0% 3.9%;
  --color-comicBubble-popover: 0 0% 100%;
  --color-comicBubble-popover-foreground: 0 0% 3.9%;
  --color-comicBubble-primary: 217 91% 60%;
  --color-comicBubble-primary-foreground: 0 0% 98%;
  --color-comicBubble-secondary: 214 100% 97%;
  --color-comicBubble-secondary-foreground: 0 0% 9%;
  --color-comicBubble-muted: 210 40% 96%;
  --color-comicBubble-muted-foreground: 0 0% 45.1%;
  --color-comicBubble-accent: 213 97% 87%;
  --color-comicBubble-accent-foreground: 0 0% 9%;
  --color-comicBubble-destructive: 0 72% 51%;
  --color-comicBubble-destructive-foreground: 0 0% 100%;
  --color-comicBubble-border: 214 32% 91%;
  --color-comicBubble-input: 214 32% 91%;
  --color-comicBubble-ring: 217 91% 60%;
}

.theme-comicBubble {
  --color-background: var(--color-comicBubble-background);
  --color-foreground: var(--color-comicBubble-foreground);
  --color-card: var(--color-comicBubble-card);
  --color-card-foreground: var(--color-comicBubble-card-foreground);
  --color-popover: var(--color-comicBubble-popover);
  --color-popover-foreground: var(--color-comicBubble-popover-foreground);
  --color-primary: var(--color-comicBubble-primary);
  --color-primary-foreground: var(--color-comicBubble-primary-foreground);
  --color-secondary: var(--color-comicBubble-secondary);
  --color-secondary-foreground: var(--color-comicBubble-secondary-foreground);
  --color-muted: var(--color-comicBubble-muted);
  --color-muted-foreground: var(--color-comicBubble-muted-foreground);
  --color-accent: var(--color-comicBubble-accent);
  --color-accent-foreground: var(--color-comicBubble-accent-foreground);
  --color-destructive: var(--color-comicBubble-destructive);
  --color-destructive-foreground: var(--color-comicBubble-destructive-foreground);
  --color-border: var(--color-comicBubble-border);
  --color-input: var(--color-comicBubble-input);
  --color-ring: var(--color-comicBubble-ring);
}

@media (prefers-color-scheme: dark) {
  .theme-comicBubble {
    --color-background: 240 10% 3.9%;
    --color-foreground: 0 0% 98%;
    --color-card: 240 10% 3.9%;
    --color-card-foreground: 0 0% 98%;
    --color-popover: 240 10% 3.9%;
    --color-popover-foreground: 0 0% 98%;
    --color-primary: 217 91% 90%;
    --color-primary-foreground: 240 5.9% 10%;
    --color-secondary: 240 3.7% 15.9%;
    --color-secondary-foreground: 0 0% 98%;
    --color-muted: 240 3.7% 15.9%;
    --color-muted-foreground: 240 5% 64.9%;
    --color-accent: 213 97% 27%;
    --color-accent-foreground: 0 0% 98%;
    --color-destructive: 0 62.8% 30.6%;
    --color-destructive-foreground: 0 0% 98%;
    --color-border: 240 3.7% 15.9%;
    --color-input: 240 3.7% 15.9%;
    --color-ring: 217 91% 90%;
  }
}

