/* Theme: comicPop */
@theme reference {
  --color-comicPop-background: 0 0% 100%;
  --color-comicPop-foreground: 0 0% 3.9%;
  --color-comicPop-card: 0 0% 100%;
  --color-comicPop-card-foreground: 0 0% 3.9%;
  --color-comicPop-popover: 0 0% 100%;
  --color-comicPop-popover-foreground: 0 0% 3.9%;
  --color-comicPop-primary: 16 100% 60%;
  --color-comicPop-primary-foreground: 0 0% 98%;
  --color-comicPop-secondary: 33 100% 96%;
  --color-comicPop-secondary-foreground: 0 0% 9%;
  --color-comicPop-muted: 210 40% 96%;
  --color-comicPop-muted-foreground: 0 0% 45.1%;
  --color-comicPop-accent: 32 98% 83%;
  --color-comicPop-accent-foreground: 0 0% 9%;
  --color-comicPop-destructive: 0 72% 51%;
  --color-comicPop-destructive-foreground: 0 0% 100%;
  --color-comicPop-border: 214 32% 91%;
  --color-comicPop-input: 214 32% 91%;
  --color-comicPop-ring: 16 100% 60%;
}

.theme-comicPop {
  --color-background: var(--color-comicPop-background);
  --color-foreground: var(--color-comicPop-foreground);
  --color-card: var(--color-comicPop-card);
  --color-card-foreground: var(--color-comicPop-card-foreground);
  --color-popover: var(--color-comicPop-popover);
  --color-popover-foreground: var(--color-comicPop-popover-foreground);
  --color-primary: var(--color-comicPop-primary);
  --color-primary-foreground: var(--color-comicPop-primary-foreground);
  --color-secondary: var(--color-comicPop-secondary);
  --color-secondary-foreground: var(--color-comicPop-secondary-foreground);
  --color-muted: var(--color-comicPop-muted);
  --color-muted-foreground: var(--color-comicPop-muted-foreground);
  --color-accent: var(--color-comicPop-accent);
  --color-accent-foreground: var(--color-comicPop-accent-foreground);
  --color-destructive: var(--color-comicPop-destructive);
  --color-destructive-foreground: var(--color-comicPop-destructive-foreground);
  --color-border: var(--color-comicPop-border);
  --color-input: var(--color-comicPop-input);
  --color-ring: var(--color-comicPop-ring);
}

@media (prefers-color-scheme: dark) {
  .theme-comicPop {
    --color-background: 240 10% 3.9%;
    --color-foreground: 0 0% 98%;
    --color-card: 240 10% 3.9%;
    --color-card-foreground: 0 0% 98%;
    --color-popover: 240 10% 3.9%;
    --color-popover-foreground: 0 0% 98%;
    --color-primary: 16 100% 90%;
    --color-primary-foreground: 240 5.9% 10%;
    --color-secondary: 240 3.7% 15.9%;
    --color-secondary-foreground: 0 0% 98%;
    --color-muted: 240 3.7% 15.9%;
    --color-muted-foreground: 240 5% 64.9%;
    --color-accent: 32 98% 23%;
    --color-accent-foreground: 0 0% 98%;
    --color-destructive: 0 62.8% 30.6%;
    --color-destructive-foreground: 0 0% 98%;
    --color-border: 240 3.7% 15.9%;
    --color-input: 240 3.7% 15.9%;
    --color-ring: 16 100% 90%;
  }
}

