/* Theme: elegantEmerald */
@theme reference {
  --color-elegantEmerald-background: 0 0% 100%;
  --color-elegantEmerald-foreground: 0 0% 3.9%;
  --color-elegantEmerald-card: 0 0% 100%;
  --color-elegantEmerald-card-foreground: 0 0% 3.9%;
  --color-elegantEmerald-popover: 0 0% 100%;
  --color-elegantEmerald-popover-foreground: 0 0% 3.9%;
  --color-elegantEmerald-primary: 161 94% 30%;
  --color-elegantEmerald-primary-foreground: 0 0% 98%;
  --color-elegantEmerald-secondary: 152 81% 96%;
  --color-elegantEmerald-secondary-foreground: 0 0% 9%;
  --color-elegantEmerald-muted: 210 40% 96%;
  --color-elegantEmerald-muted-foreground: 0 0% 45.1%;
  --color-elegantEmerald-accent: 152 76% 80%;
  --color-elegantEmerald-accent-foreground: 0 0% 9%;
  --color-elegantEmerald-destructive: 0 72% 51%;
  --color-elegantEmerald-destructive-foreground: 0 0% 100%;
  --color-elegantEmerald-border: 214 32% 91%;
  --color-elegantEmerald-input: 214 32% 91%;
  --color-elegantEmerald-ring: 161 94% 30%;
}

.theme-elegantEmerald {
  --color-background: var(--color-elegantEmerald-background);
  --color-foreground: var(--color-elegantEmerald-foreground);
  --color-card: var(--color-elegantEmerald-card);
  --color-card-foreground: var(--color-elegantEmerald-card-foreground);
  --color-popover: var(--color-elegantEmerald-popover);
  --color-popover-foreground: var(--color-elegantEmerald-popover-foreground);
  --color-primary: var(--color-elegantEmerald-primary);
  --color-primary-foreground: var(--color-elegantEmerald-primary-foreground);
  --color-secondary: var(--color-elegantEmerald-secondary);
  --color-secondary-foreground: var(--color-elegantEmerald-secondary-foreground);
  --color-muted: var(--color-elegantEmerald-muted);
  --color-muted-foreground: var(--color-elegantEmerald-muted-foreground);
  --color-accent: var(--color-elegantEmerald-accent);
  --color-accent-foreground: var(--color-elegantEmerald-accent-foreground);
  --color-destructive: var(--color-elegantEmerald-destructive);
  --color-destructive-foreground: var(--color-elegantEmerald-destructive-foreground);
  --color-border: var(--color-elegantEmerald-border);
  --color-input: var(--color-elegantEmerald-input);
  --color-ring: var(--color-elegantEmerald-ring);
}

@media (prefers-color-scheme: dark) {
  .theme-elegantEmerald {
    --color-background: 240 10% 3.9%;
    --color-foreground: 0 0% 98%;
    --color-card: 240 10% 3.9%;
    --color-card-foreground: 0 0% 98%;
    --color-popover: 240 10% 3.9%;
    --color-popover-foreground: 0 0% 98%;
    --color-primary: 161 94% 70%;
    --color-primary-foreground: 240 5.9% 10%;
    --color-secondary: 240 3.7% 15.9%;
    --color-secondary-foreground: 0 0% 98%;
    --color-muted: 240 3.7% 15.9%;
    --color-muted-foreground: 240 5% 64.9%;
    --color-accent: 152 76% 20%;
    --color-accent-foreground: 0 0% 98%;
    --color-destructive: 0 62.8% 30.6%;
    --color-destructive-foreground: 0 0% 98%;
    --color-border: 240 3.7% 15.9%;
    --color-input: 240 3.7% 15.9%;
    --color-ring: 161 94% 70%;
  }
}

