/* Theme: elegantPlatinum */
@theme reference {
  --color-elegantPlatinum-background: 0 0% 100%;
  --color-elegantPlatinum-foreground: 0 0% 3.9%;
  --color-elegantPlatinum-card: 0 0% 100%;
  --color-elegantPlatinum-card-foreground: 0 0% 3.9%;
  --color-elegantPlatinum-popover: 0 0% 100%;
  --color-elegantPlatinum-popover-foreground: 0 0% 3.9%;
  --color-elegantPlatinum-primary: 215 14% 34%;
  --color-elegantPlatinum-primary-foreground: 0 0% 98%;
  --color-elegantPlatinum-secondary: 210 20% 98%;
  --color-elegantPlatinum-secondary-foreground: 0 0% 9%;
  --color-elegantPlatinum-muted: 210 40% 96%;
  --color-elegantPlatinum-muted-foreground: 0 0% 45.1%;
  --color-elegantPlatinum-accent: 220 14% 96%;
  --color-elegantPlatinum-accent-foreground: 0 0% 9%;
  --color-elegantPlatinum-destructive: 0 72% 51%;
  --color-elegantPlatinum-destructive-foreground: 0 0% 100%;
  --color-elegantPlatinum-border: 214 32% 91%;
  --color-elegantPlatinum-input: 214 32% 91%;
  --color-elegantPlatinum-ring: 215 14% 34%;
}

.theme-elegantPlatinum {
  --color-background: var(--color-elegantPlatinum-background);
  --color-foreground: var(--color-elegantPlatinum-foreground);
  --color-card: var(--color-elegantPlatinum-card);
  --color-card-foreground: var(--color-elegantPlatinum-card-foreground);
  --color-popover: var(--color-elegantPlatinum-popover);
  --color-popover-foreground: var(--color-elegantPlatinum-popover-foreground);
  --color-primary: var(--color-elegantPlatinum-primary);
  --color-primary-foreground: var(--color-elegantPlatinum-primary-foreground);
  --color-secondary: var(--color-elegantPlatinum-secondary);
  --color-secondary-foreground: var(--color-elegantPlatinum-secondary-foreground);
  --color-muted: var(--color-elegantPlatinum-muted);
  --color-muted-foreground: var(--color-elegantPlatinum-muted-foreground);
  --color-accent: var(--color-elegantPlatinum-accent);
  --color-accent-foreground: var(--color-elegantPlatinum-accent-foreground);
  --color-destructive: var(--color-elegantPlatinum-destructive);
  --color-destructive-foreground: var(--color-elegantPlatinum-destructive-foreground);
  --color-border: var(--color-elegantPlatinum-border);
  --color-input: var(--color-elegantPlatinum-input);
  --color-ring: var(--color-elegantPlatinum-ring);
}

@media (prefers-color-scheme: dark) {
  .theme-elegantPlatinum {
    --color-background: 240 10% 3.9%;
    --color-foreground: 0 0% 98%;
    --color-card: 240 10% 3.9%;
    --color-card-foreground: 0 0% 98%;
    --color-popover: 240 10% 3.9%;
    --color-popover-foreground: 0 0% 98%;
    --color-primary: 215 14% 74%;
    --color-primary-foreground: 240 5.9% 10%;
    --color-secondary: 240 3.7% 15.9%;
    --color-secondary-foreground: 0 0% 98%;
    --color-muted: 240 3.7% 15.9%;
    --color-muted-foreground: 240 5% 64.9%;
    --color-accent: 220 14% 36%;
    --color-accent-foreground: 0 0% 98%;
    --color-destructive: 0 62.8% 30.6%;
    --color-destructive-foreground: 0 0% 98%;
    --color-border: 240 3.7% 15.9%;
    --color-input: 240 3.7% 15.9%;
    --color-ring: 215 14% 74%;
  }
}

