/* Theme: modernGradient */
@theme reference {
  --color-modernGradient-background: 0 0% 100%;
  --color-modernGradient-foreground: 0 0% 3.9%;
  --color-modernGradient-card: 0 0% 100%;
  --color-modernGradient-card-foreground: 0 0% 3.9%;
  --color-modernGradient-popover: 0 0% 100%;
  --color-modernGradient-popover-foreground: 0 0% 3.9%;
  --color-modernGradient-primary: 239 84% 67%;
  --color-modernGradient-primary-foreground: 0 0% 98%;
  --color-modernGradient-secondary: 210 40% 98%;
  --color-modernGradient-secondary-foreground: 0 0% 9%;
  --color-modernGradient-muted: 210 40% 96%;
  --color-modernGradient-muted-foreground: 0 0% 45.1%;
  --color-modernGradient-accent: 226 100% 94%;
  --color-modernGradient-accent-foreground: 0 0% 9%;
  --color-modernGradient-destructive: 0 84% 60%;
  --color-modernGradient-destructive-foreground: 0 0% 100%;
  --color-modernGradient-border: 214 32% 91%;
  --color-modernGradient-input: 214 32% 91%;
  --color-modernGradient-ring: 239 84% 67%;
}

.theme-modernGradient {
  --color-background: var(--color-modernGradient-background);
  --color-foreground: var(--color-modernGradient-foreground);
  --color-card: var(--color-modernGradient-card);
  --color-card-foreground: var(--color-modernGradient-card-foreground);
  --color-popover: var(--color-modernGradient-popover);
  --color-popover-foreground: var(--color-modernGradient-popover-foreground);
  --color-primary: var(--color-modernGradient-primary);
  --color-primary-foreground: var(--color-modernGradient-primary-foreground);
  --color-secondary: var(--color-modernGradient-secondary);
  --color-secondary-foreground: var(--color-modernGradient-secondary-foreground);
  --color-muted: var(--color-modernGradient-muted);
  --color-muted-foreground: var(--color-modernGradient-muted-foreground);
  --color-accent: var(--color-modernGradient-accent);
  --color-accent-foreground: var(--color-modernGradient-accent-foreground);
  --color-destructive: var(--color-modernGradient-destructive);
  --color-destructive-foreground: var(--color-modernGradient-destructive-foreground);
  --color-border: var(--color-modernGradient-border);
  --color-input: var(--color-modernGradient-input);
  --color-ring: var(--color-modernGradient-ring);
}

@media (prefers-color-scheme: dark) {
  .theme-modernGradient {
    --color-background: 240 10% 3.9%;
    --color-foreground: 0 0% 98%;
    --color-card: 240 10% 3.9%;
    --color-card-foreground: 0 0% 98%;
    --color-popover: 240 10% 3.9%;
    --color-popover-foreground: 0 0% 98%;
    --color-primary: 239 84% 90%;
    --color-primary-foreground: 240 5.9% 10%;
    --color-secondary: 240 3.7% 15.9%;
    --color-secondary-foreground: 0 0% 98%;
    --color-muted: 240 3.7% 15.9%;
    --color-muted-foreground: 240 5% 64.9%;
    --color-accent: 226 100% 34%;
    --color-accent-foreground: 0 0% 98%;
    --color-destructive: 0 62.8% 30.6%;
    --color-destructive-foreground: 0 0% 98%;
    --color-border: 240 3.7% 15.9%;
    --color-input: 240 3.7% 15.9%;
    --color-ring: 239 84% 90%;
  }
}

