/* Theme: modernNeon */
@theme reference {
  --color-modernNeon-background: 0 0% 100%;
  --color-modernNeon-foreground: 0 0% 3.9%;
  --color-modernNeon-card: 0 0% 100%;
  --color-modernNeon-card-foreground: 0 0% 3.9%;
  --color-modernNeon-popover: 0 0% 100%;
  --color-modernNeon-popover-foreground: 0 0% 3.9%;
  --color-modernNeon-primary: 258 90% 66%;
  --color-modernNeon-primary-foreground: 0 0% 98%;
  --color-modernNeon-secondary: 270 100% 98%;
  --color-modernNeon-secondary-foreground: 0 0% 9%;
  --color-modernNeon-muted: 210 40% 96%;
  --color-modernNeon-muted-foreground: 0 0% 45.1%;
  --color-modernNeon-accent: 269 100% 92%;
  --color-modernNeon-accent-foreground: 0 0% 9%;
  --color-modernNeon-destructive: 0 84% 60%;
  --color-modernNeon-destructive-foreground: 0 0% 100%;
  --color-modernNeon-border: 214 32% 91%;
  --color-modernNeon-input: 214 32% 91%;
  --color-modernNeon-ring: 258 90% 66%;
}

.theme-modernNeon {
  --color-background: var(--color-modernNeon-background);
  --color-foreground: var(--color-modernNeon-foreground);
  --color-card: var(--color-modernNeon-card);
  --color-card-foreground: var(--color-modernNeon-card-foreground);
  --color-popover: var(--color-modernNeon-popover);
  --color-popover-foreground: var(--color-modernNeon-popover-foreground);
  --color-primary: var(--color-modernNeon-primary);
  --color-primary-foreground: var(--color-modernNeon-primary-foreground);
  --color-secondary: var(--color-modernNeon-secondary);
  --color-secondary-foreground: var(--color-modernNeon-secondary-foreground);
  --color-muted: var(--color-modernNeon-muted);
  --color-muted-foreground: var(--color-modernNeon-muted-foreground);
  --color-accent: var(--color-modernNeon-accent);
  --color-accent-foreground: var(--color-modernNeon-accent-foreground);
  --color-destructive: var(--color-modernNeon-destructive);
  --color-destructive-foreground: var(--color-modernNeon-destructive-foreground);
  --color-border: var(--color-modernNeon-border);
  --color-input: var(--color-modernNeon-input);
  --color-ring: var(--color-modernNeon-ring);
}

@media (prefers-color-scheme: dark) {
  .theme-modernNeon {
    --color-background: 240 10% 3.9%;
    --color-foreground: 0 0% 98%;
    --color-card: 240 10% 3.9%;
    --color-card-foreground: 0 0% 98%;
    --color-popover: 240 10% 3.9%;
    --color-popover-foreground: 0 0% 98%;
    --color-primary: 258 90% 90%;
    --color-primary-foreground: 240 5.9% 10%;
    --color-secondary: 240 3.7% 15.9%;
    --color-secondary-foreground: 0 0% 98%;
    --color-muted: 240 3.7% 15.9%;
    --color-muted-foreground: 240 5% 64.9%;
    --color-accent: 269 100% 32%;
    --color-accent-foreground: 0 0% 98%;
    --color-destructive: 0 62.8% 30.6%;
    --color-destructive-foreground: 0 0% 98%;
    --color-border: 240 3.7% 15.9%;
    --color-input: 240 3.7% 15.9%;
    --color-ring: 258 90% 90%;
  }
}

