/* Theme: royalPurple */
@theme reference {
  --color-royalPurple-background: 0 0% 100%;
  --color-royalPurple-foreground: 0 0% 3.9%;
  --color-royalPurple-card: 0 0% 100%;
  --color-royalPurple-card-foreground: 0 0% 3.9%;
  --color-royalPurple-popover: 0 0% 100%;
  --color-royalPurple-popover-foreground: 0 0% 3.9%;
  --color-royalPurple-primary: 271 81% 56%;
  --color-royalPurple-primary-foreground: 0 0% 98%;
  --color-royalPurple-secondary: 270 100% 98%;
  --color-royalPurple-secondary-foreground: 0 0% 9%;
  --color-royalPurple-muted: 210 40% 96%;
  --color-royalPurple-muted-foreground: 0 0% 45.1%;
  --color-royalPurple-accent: 269 100% 92%;
  --color-royalPurple-accent-foreground: 0 0% 9%;
  --color-royalPurple-destructive: 0 72% 51%;
  --color-royalPurple-destructive-foreground: 0 0% 100%;
  --color-royalPurple-border: 214 32% 91%;
  --color-royalPurple-input: 214 32% 91%;
  --color-royalPurple-ring: 271 81% 56%;
}

.theme-royalPurple {
  --color-background: var(--color-royalPurple-background);
  --color-foreground: var(--color-royalPurple-foreground);
  --color-card: var(--color-royalPurple-card);
  --color-card-foreground: var(--color-royalPurple-card-foreground);
  --color-popover: var(--color-royalPurple-popover);
  --color-popover-foreground: var(--color-royalPurple-popover-foreground);
  --color-primary: var(--color-royalPurple-primary);
  --color-primary-foreground: var(--color-royalPurple-primary-foreground);
  --color-secondary: var(--color-royalPurple-secondary);
  --color-secondary-foreground: var(--color-royalPurple-secondary-foreground);
  --color-muted: var(--color-royalPurple-muted);
  --color-muted-foreground: var(--color-royalPurple-muted-foreground);
  --color-accent: var(--color-royalPurple-accent);
  --color-accent-foreground: var(--color-royalPurple-accent-foreground);
  --color-destructive: var(--color-royalPurple-destructive);
  --color-destructive-foreground: var(--color-royalPurple-destructive-foreground);
  --color-border: var(--color-royalPurple-border);
  --color-input: var(--color-royalPurple-input);
  --color-ring: var(--color-royalPurple-ring);
}

@media (prefers-color-scheme: dark) {
  .theme-royalPurple {
    --color-background: 240 10% 3.9%;
    --color-foreground: 0 0% 98%;
    --color-card: 240 10% 3.9%;
    --color-card-foreground: 0 0% 98%;
    --color-popover: 240 10% 3.9%;
    --color-popover-foreground: 0 0% 98%;
    --color-primary: 271 81% 90%;
    --color-primary-foreground: 240 5.9% 10%;
    --color-secondary: 240 3.7% 15.9%;
    --color-secondary-foreground: 0 0% 98%;
    --color-muted: 240 3.7% 15.9%;
    --color-muted-foreground: 240 5% 64.9%;
    --color-accent: 269 100% 32%;
    --color-accent-foreground: 0 0% 98%;
    --color-destructive: 0 62.8% 30.6%;
    --color-destructive-foreground: 0 0% 98%;
    --color-border: 240 3.7% 15.9%;
    --color-input: 240 3.7% 15.9%;
    --color-ring: 271 81% 90%;
  }
}

